import * as types from "../../actionTypes";
import DailyReportApi from "../../../api/DailyReportApi";

let actionsFunction = {

  //CRUD METHODS

  // Create dailyreport
  createDailyReport: function(dailyreport) {
    return function(dispatch) {
      return DailyReportApi
        .createDailyReport(dailyreport)
        .then(dailyreport => {
          dispatch(actionsFunction.createDailyReportSuccess(dailyreport));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  createDailyReportSuccess: function(dailyreport) {
    return { type: types.CREATE_DAILYREPORT_SUCCESS, payload: dailyreport };
  },


  // Delete dailyreport
  deleteDailyReport: function(id) {
    return function(dispatch) {
      return DailyReportApi
        .deleteDailyReport(id)
        .then(dailyreport => {
          dispatch(actionsFunction.deleteDailyReportSuccess(dailyreport));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  deleteDailyReportSuccess: function(dailyreport) {
    return { type: types.DELETE_DAILYREPORT_SUCCESS, payload: dailyreport };
  },


  // Get dailyreport
  loadDailyReport: function(id) {
    return function(dispatch) {
      return DailyReportApi
        .getOneDailyReport(id)
        .then(dailyreport => {
          dispatch(actionsFunction.loadDailyReportSuccess(dailyreport));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadDailyReportSuccess: function(dailyreport) {
    return { type: types.GET_DAILYREPORT_SUCCESS, payload: dailyreport };
  },

  // Load  list
  loadDailyReportList: function() {
    return function(dispatch) {
      return DailyReportApi
        .getDailyReportList()
        .then(list => {
          dispatch(actionsFunction.loadDailyReportListSuccess(list));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  loadDailyReportListSuccess: function(list) {
    return { type: types.LIST_DAILYREPORT_SUCCESS, payload: list };
  },

	
  // Save dailyreport
  saveDailyReport: function(dailyreport) {
    return function(dispatch) {
      return DailyReportApi
        .saveDailyReport(dailyreport)
        .then(dailyreport => {
          dispatch(actionsFunction.saveDailyReportSuccess(dailyreport));
        })
        .catch(error => {
          throw error;
        });
    };
  },

  saveDailyReportSuccess: function(dailyreport) {
    return { type: types.UPDATE_DAILYREPORT_SUCCESS, payload: dailyreport };
  },


};

export default actionsFunction;
